import React from "react"
import { useAuth } from "../contexts/AuthContext"
import { useNavigate, Link } from "react-router-dom"
import { GraduationCap, Layout, BookOpen, Settings, LogOut } from "lucide-react"
import { NotificationCenter } from "./NotificationCenter"
import { ThemeToggle } from "./ThemeToggle"

export function NavBar() {
  const navigate = useNavigate()
  const { user, logout } = useAuth()

  const handleSignOut = async () => {
    logout()
    navigate("/")
  }

  return (
    <nav className="navbar">
      <div className="navbar-content">
        <div className="navbar-logo">
          <GraduationCap className="logo-icon" />
          <span className="logo-text">EduPlatform</span>
        </div>

        {user && (
          <div className="navbar-links">
            <NavButton to="/dashboard" icon={Layout} label="Dashboard" />
            <NavButton to="/courses" icon={BookOpen} label="Courses" />
            <NavButton to="/settings" icon={Settings} label="Settings" />
            <NotificationCenter />
            <ThemeToggle />
            <NavButton onClick={handleSignOut} icon={LogOut} label="Sign Out" />
          </div>
        )}
      </div>
    </nav>
  )
}

function NavButton({ to, icon: Icon, label, onClick }) {
  const buttonProps = to ? { to, as: Link } : { onClick }

  return (
    <button {...buttonProps} className="nav-button">
      <Icon className="nav-icon" />
      <span className="nav-label">{label}</span>
    </button>
  )
}
