import React, { useState } from "react"
import { NotificationCenter } from "./NotificationCenter"
import { useNavigate, Link } from "react-router-dom"
import { GraduationCap, Layout, BookOpen, Settings, LogOut, Bell, Home } from "lucide-react"
import { useAuth } from "../App"
import './NavBar.css'

export function NavBar() {
  const navigate = useNavigate()
  const { user, logout } = useAuth()
  const [showNotifications, setShowNotifications] = useState(false)

  const handleSignOut = () => {
    logout()
    navigate("/")
  }

  const toggleNotifications = () => {
    setShowNotifications(!showNotifications)
  }

  return (
    <nav className="navbar">
      <div className="navbar-content">
        <div className="navbar-logo">
          <GraduationCap className="logo-icon" />
          <span className="logo-text">EduPlatform</span>
        </div>

        {user && (
          <div className="navbar-links">
            <NavButton to="/" icon={Home} label="Home" />
            <NavButton to="/dashboard" icon={Layout} label="Dashboard" />
            <NavButton to="/courses" icon={BookOpen} label="Courses" />
            <NavButton to="/settings" icon={Settings} label="Settings" />
            <button onClick={toggleNotifications} className="nav-button">
              <Bell />
              <span>Notifications</span>
            </button>
            <NavButton onClick={handleSignOut} icon={LogOut} label="Sign Out" />
          </div>
        )}
      </div>
      {showNotifications && (
        <div className="notification-popup">
          <NotificationCenter />
        </div>
      )}
    </nav>
  )
}

function NavButton({ to, icon: Icon, label, onClick }) {
  if (onClick) {
    return (
      <button onClick={onClick} className="nav-button">
        <Icon />
        <span>{label}</span>
      </button>
    )
  }
  return (
    <Link to={to} className="nav-button">
      <Icon />
      <span>{label}</span>
    </Link>
  )
}
