import React, { useState, useEffect } from "react";
import { BookOpen, Users, Search, Filter, Star, Clock, PlayCircle, DollarSign, Award, Bookmark } from "lucide-react"

// ... (keep the interface definitions as they are)

export function CoursesPage() {
  const [loading, setLoading] = useState(true);
  // const [categories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedLevel, setSelectedLevel] = useState("");
  const [categories] = useState(['Web Development', 'Data Science', 'Mobile Development']);
  const [levels] = useState(['Beginner', 'Intermediate', 'Advanced']);
  const [filteredCourses, setFilteredCourses] = useState([]);

  useEffect(() => {
    // Fetch data and update state variables
    setLoading(false);
  }, []);

  useEffect(() => {
    // Logic to filter courses based on categories, levels, or other criteria
    // This is just an example, adjust according to your actual filtering logic
    setFilteredCourses(courses.filter(course => 
      categories.includes(course.category) && levels.includes(course.level)
    ));
  }, [categories, levels, courses]);

  const handleEnroll = (courseId) => {
    // Enrollment logic here
  };

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner"></div>
      </div>
    )
  }

  return (
    <div className="courses-page">
      {/* Featured Categories */}
      <div className="featured-categories">
        <h2>Featured Categories</h2>
        <div className="category-grid">
          {categories.slice(1).map((category) => (
            <button key={category.id} onClick={() => setSelectedCategory(category.id)} className="category-button">
              <div className="category-icon">
                {category.id === "programming" && <BookOpen />}
                {category.id === "design" && <PlayCircle />}
                {category.id === "business" && <DollarSign />}
                {category.id === "marketing" && <Users />}
              </div>
              <span>{category.name}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Search and Filters */}
      <div className="search-filters">
        <div className="search-bar">
          <Search className="search-icon" />
          <input
            type="text"
            placeholder="Search courses..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        </div>

        <div className="filters">
          <div className="filter">
            <Filter className="filter-icon" />
            <select value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)}>
              {categories.map((category) => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>

          <select value={selectedLevel} onChange={(e) => setSelectedLevel(e.target.value)}>
            {levels.map((level) => (
              <option key={level.id} value={level.id}>
                {level.name}
              </option>
            ))}
          </select>

          <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
            <option value="popular">Most Popular</option>
            <option value="rating">Highest Rated</option>
            <option value="price-low">Price: Low to High</option>
            <option value="price-high">Price: High to Low</option>
          </select>
        </div>
      </div>

      {/* Course Grid */}
      <div className="course-grid">
        {filteredCourses.map((course) => (
          <div key={course.id} className="course-card">
            <div className="course-image">
              <img
                src={course.image_url || "https://images.unsplash.com/photo-1516321318423-f06f85e504b3"}
                alt={course.title}
              />
              <div className="course-rating">
                <Star className="star-icon" />
                {course.rating.toFixed(1)}
              </div>
              <button className="bookmark-button">
                <Bookmark className="bookmark-icon" />
              </button>
              <div className="course-level">
                <span>{course.level}</span>
              </div>
            </div>

            <div className="course-content">
              <div className="course-instructor">
                <Award className="instructor-icon" />
                <span>{course.instructor}</span>
              </div>

              <h3>{course.title}</h3>
              <p>{course.description}</p>

              <div className="course-meta">
                <div className="course-duration">
                  <Clock className="duration-icon" />
                  <span>{course.duration}</span>
                </div>
                <div className="course-students">
                  <Users className="students-icon" />
                  <span>{course.students_count} Students</span>
                </div>
              </div>

              <div className="course-footer">
                <div className="course-price">${course.price.toFixed(2)}</div>
                <button onClick={() => handleEnroll(course.id)} className="enroll-button">
                  Enroll Now
                </button>
              </div>
            </div>
          </div>
        ))}

        {filteredCourses.length === 0 && (
          <div className="no-courses">
            <BookOpen className="no-courses-icon" />
            <h3>No courses found</h3>
            <p>Try adjusting your search or filter criteria</p>
          </div>
        )}
      </div>
    </div>
  )
}
