import React, { useState, useEffect } from "react";
import { BookOpen, Award, Clock, BarChart, BookMarked, Target, Zap, Trophy, Users, Star } from "lucide-react";
import { CourseProgressTracker } from "./CourseProgressTracker"
import "./Dashboard.css";
// ... (keep the interface definitions as they are)

export function Dashboard() {
  const [loading, setLoading] = useState(true);
  // eslint-disable-next-line no-unused-vars
  const [stats, setStats] = useState({ completedCourses: 5, totalHours: 120 });
  const [enrollments] = useState([/* Your enrollment data */]);
  const [achievements] = useState([/* Your achievement data */]);
  const [recommendedCourses] = useState([/* Your recommended courses data */]);
  const [courseProgress] = useState([/* Your course progress data */]);

  useEffect(() => {
    // Fetch stats data from an API
    // const fetchedStats = await fetchStatsFromAPI();
    // setStats(fetchedStats);
  }, []);

  // Repeat similar useEffect hooks for other state variables if needed

  useEffect(() => {
    // Fetch data and update state variables
    setLoading(false);
  }, []);

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner"></div>
      </div>
    )
  }

  return (
    <div className="dashboard">
      {/* Welcome Section */}
      <div className="welcome-section">
        <div className="welcome-content">
          <h1>Welcome back!</h1>
          <p>Ready to continue your learning journey?</p>
        </div>
        <div className="streak-card">
          <div className="streak-content">
            <Zap className="streak-icon" />
            <span>{stats.streak} Day Streak!</span>
          </div>
        </div>
      </div>

      {/* Stats Grid */}
      <div className="stats-grid">
        <StatCard icon={BookMarked} title="Active Courses" value={stats.totalCourses} color="blue" />
        <StatCard icon={Award} title="Completed" value={stats.completedCourses} color="green" />
        <StatCard icon={Clock} title="Learning Hours" value={`${stats.totalHours}h`} color="purple" />
        <StatCard icon={BarChart} title="Avg. Progress" value={`${stats.averageProgress}%`} color="indigo" />
      </div>

      {/* Main Content Grid */}
      <div className="main-content">
        {/* Active Courses */}
        <div className="active-courses">
          <h2>Active Courses</h2>
          <div className="course-list">
            {enrollments.map((enrollment) => (
              <div key={enrollment.course.id} className="course-item">
                <div className="course-header">
                  <img
                    src={enrollment.course.image_url || "https://images.unsplash.com/photo-1516321318423-f06f85e504b3"}
                    alt={enrollment.course.title}
                  />
                  <div className="course-info">
                    <h3>{enrollment.course.title}</h3>
                    <div className="course-rating">
                      <Star className="rating-icon" />
                      <span>{enrollment.course.rating}</span>
                    </div>
                  </div>
                </div>

                <div className="course-instructor">
                  <Users className="instructor-icon" />
                  <span>{enrollment.course.instructor}</span>
                </div>

                <div className="course-progress">
                  <div className="progress-item">
                    <Target className="progress-icon" />
                    <span>Progress: {enrollment.progress}%</span>
                  </div>
                  <div className="progress-item">
                    <BookOpen className="progress-icon" />
                    <span>
                      {enrollment.completed_lessons}/{enrollment.total_lessons} Lessons
                    </span>
                  </div>
                </div>

                <div className="progress-bar">
                  <div className="progress-fill" style={{ width: `${enrollment.progress}%` }} />
                </div>

                <button className="continue-button">Continue Learning</button>
              </div>
            ))}

            {enrollments.length === 0 && (
              <div className="no-courses">
                <BookOpen className="no-courses-icon" />
                <h3>No active courses</h3>
                <p>Start learning by enrolling in a course</p>
              </div>
            )}
          </div>
        </div>

        {/* Sidebar */}
        <div className="dashboard-sidebar">
          {/* Achievements */}
          <div className="achievements">
            <h2>Achievements</h2>
            <div className="achievement-list">
              {achievements.map((achievement) => (
                <div key={achievement.id} className="achievement-item">
                  <div className="achievement-icon">
                    <Trophy className="trophy-icon" />
                  </div>
                  <div className="achievement-info">
                    <h3>{achievement.title}</h3>
                    <p>{achievement.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Recommended Courses */}
          <div className="recommended-courses">
            <h2>Recommended</h2>
            <div className="recommended-list">
              {recommendedCourses.map((course) => (
                <div key={course.id} className="recommended-item">
                  <img src={course.image_url || "/placeholder.svg"} alt={course.title} />
                  <div className="recommended-info">
                    <h3>{course.title}</h3>
                    <p>{course.instructor}</p>
                    <div className="course-meta">
                      <div className="course-rating">
                        <Star className="rating-icon" />
                        <span>{course.rating}</span>
                      </div>
                      <div className="course-duration">
                        <Clock className="duration-icon" />
                        <span>{course.duration}</span>
                      </div>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      {/* Course Progress Tracker */}
      <div className="course-progress-section">
        <div className="progress-tracker">
          <CourseProgressTracker courses={courseProgress} />
        </div>
      </div>
    </div>
  )
}

