import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { motion } from 'framer-motion';
import { PlayCircle, Clock, Users, BookOpen } from 'lucide-react';
import { SAMPLE_COURSES } from '../data/sampleData';  // Add this import
import './CourseDetails.css';

export default function CourseDetails() {
  const { courseId } = useParams();
  const [course, setCourse] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulating API call to get course details
    // Replace this with your actual API call
    const fetchCourse = async () => {
      // For now, we'll use the sample course data
      const sampleCourse = SAMPLE_COURSES.find(c => c.id === parseInt(courseId));
      setCourse(sampleCourse);
      setLoading(false);
    };

    fetchCourse();
  }, [courseId]);

  if (loading || !course) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <motion.div 
      className="course-details"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <div className="course-header">
        <div className="course-header-content">
          <h1>{course.title}</h1>
          <p>{course.description}</p>
          <div className="course-meta-info">
            <div className="meta-item">
              <Clock />
              <span>{course.duration}</span>
            </div>
            <div className="meta-item">
              <Users />
              <span>{course.students_count} students</span>
            </div>
            <div className="meta-item">
              <BookOpen />
              <span>{course.level}</span>
            </div>
          </div>
        </div>
      </div>

      <div className="course-content-container">
        <div className="start-learning">
          <button className="start-button">
            <PlayCircle />
            Start Learning
          </button>
        </div>

        <div className="course-modules">
          <h2>Course Content</h2>
          {/* Add your course modules/lessons here */}
          <div className="modules-list">
            <div className="module">
              <h3>Module 1: Introduction</h3>
              {/* Add module content */}
            </div>
            {/* Add more modules */}
          </div>
        </div>
      </div>
    </motion.div>
  );
}
