import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from "framer-motion";
import { BookOpen, Clock, Target, Zap, Trophy, Users, Star } from "lucide-react";
import { useAuth } from "../App";
import { SAMPLE_COURSES } from '../data/sampleData';
import { COURSE_MODULES } from '../data/courseModules';  // Add this import
import "./Dashboard.css";

export function Dashboard() {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ completedCourses: 5, totalHours: 120 });
  const [enrollments] = useState([/* Your enrollment data */]);
  const [achievements] = useState([/* Your achievement data */]);
  const [recommendedCourses] = useState([/* Your recommended courses data */]);
  const [inProgressCourses, setInProgressCourses] = useState([]);
  const [currentCourses, setCurrentCourses] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch stats data from an API
    // const fetchedStats = await fetchStatsFromAPI();
    // setStats(fetchedStats);
  }, []);

  // Repeat similar useEffect hooks for other state variables if needed

  useEffect(() => {
    // Fetch data and update state variables
    setLoading(false);
  }, []);

  useEffect(() => {
    const courses = JSON.parse(localStorage.getItem('inProgressCourses') || '[]');
    setInProgressCourses(courses.sort((a, b) => 
      new Date(b.lastAccessed) - new Date(a.lastAccessed)
    ));
  }, []);

  useEffect(() => {
    const inProgressCourses = JSON.parse(localStorage.getItem('inProgressCourses') || '[]');
    // Merge the progress data with the full course details
    const coursesWithDetails = inProgressCourses.map(progressCourse => {
      const fullCourse = SAMPLE_COURSES.find(c => c.id === progressCourse.id);
      return {
        ...fullCourse,
        progress: progressCourse.progress,
        lastAccessed: progressCourse.lastAccessed
      };
    });
    setCurrentCourses(coursesWithDetails);
  }, []);

  const handleContinueCourse = (courseId) => {
    navigate(`/courses/${courseId}`);
  };

  const resumeCourse = (courseId) => {
    // Get the course modules
    const courseModules = COURSE_MODULES[courseId];
    if (courseModules && courseModules.length > 0) {
      // Navigate to first module, first lesson
      const firstModule = courseModules[0];
      const firstLesson = firstModule.lessons[0];
      navigate(`/courses/${courseId}/learn/${firstModule.id}/${firstLesson.id}`);
    }
  };

  if (loading) {
    return (
      <div className="loading-spinner">
        <div className="spinner"></div>
      </div>
    )
  }

  return (
    <motion.div 
      className="dashboard"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.5 }}
    >
      <section className="welcome-section">
        <motion.div 
          className="welcome-content"
          initial={{ x: -20 }}
          animate={{ x: 0 }}
          transition={{ delay: 0.2 }}
        >
          <h1>Welcome back, {user?.name}!</h1>
          <p className="welcome-subtitle">Ready to continue your learning journey?</p>
          <div className="welcome-stats">
            <motion.div 
              className="streak-badge"
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <Zap className="streak-icon" />
              <span className="streak-text">{stats.streak} Day Streak!</span>
            </motion.div>
          </div>
          <div className="quick-actions">
            <button className="action-button">Resume Learning</button>
            <button className="action-button">Browse Courses</button>
          </div>
          <div className="streak-days">
            {[...Array(7)].map((_, i) => (
              <div key={i} className={`streak-day ${i < 5 ? 'completed' : ''}`}>
                {i + 1}
              </div>
            ))}
          </div>
        </motion.div>
      </section>

      <motion.div 
        className="stats-grid"
        initial={{ y: 20, opacity: 0 }}
        animate={{ y: 0, opacity: 1 }}
        transition={{ delay: 0.4 }}
      >
        <StatCard 
          icon={BookOpen} 
          title="Active Courses" 
          value={stats.activeCourses} 
          trend="+2 this week"
        />
        <StatCard 
          icon={Target} 
          title="Completion Rate" 
          value={`${stats.completionRate}%`}
          trend="5% increase" 
        />
        <StatCard 
          icon={Clock} 
          title="Learning Hours" 
          value={stats.totalHours}
          trend="12hrs this week" 
        />
        <StatCard 
          icon={Trophy} 
          title="Achievements" 
          value={stats.achievements}
          trend="2 new" 
        />
      </motion.div>

      <div className="dashboard-content">
        <motion.div 
          className="main-content"
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ delay: 0.6 }}
        >
          <div className="active-courses">
            <header className="section-header">
              <h2>Current Courses</h2>
              <button className="view-all">View All</button>
            </header>
            <div className="course-list">
              {enrollments.map((enrollment) => (
                <div key={enrollment.course.id} className="course-item">
                  <div className="course-header">
                    <img
                      src={enrollment.course.image_url || "https://images.unsplash.com/photo-1516321318423-f06f85e504b3"}
                      alt={enrollment.course.title}
                    />
                    <div className="course-info">
                      <h3>{enrollment.course.title}</h3>
                      <div className="course-rating">
                        <Star className="rating-icon" />
                        <span>{enrollment.course.rating}</span>
                      </div>
                    </div>
                  </div>

                  <div className="course-instructor">
                    <Users className="instructor-icon" />
                    <span>{enrollment.course.instructor}</span>
                  </div>

                  <div className="course-progress">
                    <div className="progress-item">
                      <Target className="progress-icon" />
                      <span>Progress: {enrollment.progress}%</span>
                    </div>
                    <div className="progress-item">
                      <BookOpen className="progress-icon" />
                      <span>
                        {enrollment.completed_lessons}/{enrollment.total_lessons} Lessons
                      </span>
                    </div>
                  </div>

                  <div className="progress-bar">
                    <div className="progress-fill" style={{ width: `${enrollment.progress}%` }} />
                  </div>

                  <button className="continue-button">Continue Learning</button>
                </div>
              ))}

              {enrollments.length === 0 && (
                <div className="no-courses">
                  <BookOpen className="no-courses-icon" />
                  <h3>No active courses</h3>
                  <p>Start learning by enrolling in a course</p>
                </div>
              )}
            </div>
            <div className="course-progress-details">
              <h4>Today's Goal</h4>
              <p>Complete 2 more lessons to reach your daily target</p>
            </div>
            <div className="course-tags">
              <span className="course-tag">Python</span>
              <span className="course-tag">Web Development</span>
              <span className="course-tag">Data Science</span>
            </div>
          </div>
        </motion.div>

        <motion.div 
          className="dashboard-sidebar"
          initial={{ x: 20, opacity: 0 }}
          animate={{ x: 0, opacity: 1 }}
          transition={{ delay: 0.8 }}
        >
          <div className="achievements-section">
            <h2>Latest Achievements</h2>
            <div className="achievement-list">
              {achievements.map((achievement) => (
                <div key={achievement.id} className="achievement-item">
                  <div className="achievement-icon">
                    <Trophy className="trophy-icon" />
                  </div>
                  <div className="achievement-info">
                    <h3>{achievement.title}</h3>
                    <p>{achievement.description}</p>
                  </div>
                  <div className="achievement-progress">
                    <div 
                      className="achievement-progress-fill" 
                      style={{ width: '75%' }}
                    />
                  </div>
                </div>
              ))}
            </div>

            <div className="recommended-courses">
              <h2>Recommended</h2>
              <div className="recommended-list">
                {recommendedCourses.map((course) => (
                  <div key={course.id} className="recommended-item">
                    <img src={course.image_url || "/placeholder.svg"} alt={course.title} />
                    <div className="recommended-info">
                      <h3>{course.title}</h3>
                      <p>{course.instructor}</p>
                      <div className="course-meta">
                        <div className="course-rating">
                          <Star className="rating-icon" />
                          <span>{course.rating}</span>
                        </div>
                        <div className="course-duration">
                          <Clock className="duration-icon" />
                          <span>{course.duration}</span>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </div>
          <div className="learning-streak">
            <h3>Weekly Progress</h3>
            <p>You're on track to meet your goals!</p>
            <div className="streak-progress">
              {/* Add weekly progress visualization */}
            </div>
          </div>
        </motion.div>
      </div>

      <section className="in-progress-courses">
        <h2>Courses in Progress</h2>
        <div className="courses-grid">
          {inProgressCourses.map(course => (
            <div key={course.id} className="course-card">
              <div className="course-info">
                <h3>{course.title}</h3>
                <div className="progress-bar">
                  <div 
                    className="progress-fill" 
                    style={{ width: `${course.progress}%` }}
                  />
                </div>
                <p className="progress-text">{course.progress}% complete</p>
              </div>
              <button 
                className="continue-button"
                onClick={() => handleContinueCourse(course.id)}
              >
                Continue Learning
              </button>
            </div>
          ))}

          {inProgressCourses.length === 0 && (
            <div className="no-courses">
              <BookOpen size={48} />
              <h3>No courses in progress</h3>
              <p>Start learning by enrolling in a course!</p>
            </div>
          )}
        </div>
      </section>

      <div className="dashboard-content">
        <motion.div className="main-content">
          <div className="active-courses">
            <header className="section-header">
              <h2>Current Courses</h2>
            </header>
            <div className="course-list">
              {currentCourses.map((course) => (
                <motion.div 
                  key={course.id} 
                  className="course-item"
                  whileHover={{ scale: 1.02 }}
                  transition={{ type: "spring", stiffness: 300 }}
                >
                  <div className="course-header">
                    <img src={course.image_url} alt={course.title} />
                    <div className="course-info">
                      <h3>{course.title}</h3>
                      <p>{course.instructor}</p>
                    </div>
                  </div>

                  <div className="course-progress">
                    <div className="progress-bar">
                      <div 
                        className="progress-fill" 
                        style={{ width: `${course.progress}%` }}
                      />
                    </div>
                    <span className="progress-text">{course.progress}% Complete</span>
                  </div>

                  <button 
                    className="resume-button"
                    onClick={() => resumeCourse(course.id)}
                  >
                    Resume Course
                  </button>
                </motion.div>
              ))}

              {currentCourses.length === 0 && (
                <div className="no-courses">
                  <BookOpen size={48} />
                  <h3>No courses in progress</h3>
                  <p>Start learning by enrolling in a course!</p>
                </div>
              )}
            </div>
          </div>
        </motion.div>
        {/* ...rest of the dashboard content... */}
      </div>
    </motion.div>
  );
}

function StatCard({ icon: Icon, title, value, trend }) {
  return (
    <motion.div 
      className="stat-card"
      whileHover={{ scale: 1.05 }}
      transition={{ type: "spring", stiffness: 300 }}
    >
      <div className="stat-icon">
        <Icon size={24} />
      </div>
      <div className="stat-info">
        <h3>{title}</h3>
        <p className="stat-value">{value}</p>
        {trend && <span className="stat-trend">{trend}</span>}
      </div>
    </motion.div>
  );
}
